import { useContext, useEffect, useState } from "react";
import UserContext from "../UserContext";

export default function WebSocketCall({ socket }) {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const user = useContext(UserContext)

  const handleText = (e) => {
    const inputMessage = e.target.value;
    setMessage(inputMessage);
  };

  const handleSubmit = () => {
    if (!message) {
      return;
    }
    socket.emit("data", { message, user,});
    setMessage("");
  };

  const handleVote = (id, action) => {
    // Find the message in the state and update its votes locally
    const updatedMessages = messages.map((msg) => {
      if (msg.ulid === id) {
        const upvotes = msg.upvotes || 0; // Initialize to 0 if undefined
        const downvotes = msg.downvotes || 0; // Initialize to 0 if undefined

        if (action === "upvote") {
          return { ...msg, upvotes: upvotes + 1 };
        } else if (action === "downvote") {
          return { ...msg, downvotes: downvotes + 1 };
        }
      }
      return msg;
    });

    setMessages(updatedMessages);

    // Emit the vote action to the server
    socket.emit("data", { action, ulid: id });
  };



  useEffect(() => {

    socket.on("data", (data) => {
      console.log('data',data)
      setMessages((prevMessages) => {
        const updatedMessages = [...prevMessages];
        const existingIndex = updatedMessages.findIndex((msg) => msg.ulid === data.ulid);

        if (existingIndex !== -1) {
          updatedMessages[existingIndex] = data;
        } else {
          updatedMessages.push(data);
        }

        return updatedMessages;
      });
    });


    return () => {
      socket.off("data", () => {
        console.log("data event was removed");
      });

    };
  }, [socket, messages]);

  return (
    <div>
      <h2>WebSocket Communication</h2>
      <input type="text" value={message} onChange={handleText} />
      <button onClick={handleSubmit}>submit</button>
      <ul>
        {messages.map((msg, ind) => (
          <li key={ind}>
            {msg.user}: {msg.message} ::{msg.upvotes} upvotes, {msg.downvotes} downvotes {msg.id}
            <button onClick={() => handleVote(msg.ulid, "upvote")}>Upvote</button>
            <button onClick={() => handleVote(msg.ulid, "downvote")}>Downvote</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
