import { useContext, useEffect, useState } from "react";
import UserContext from "../UserContext";

export default function WebSocketCall({ socket }) {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const user = useContext(UserContext)

  const handleText = (e) => {
    const inputMessage = e.target.value;
    setMessage(inputMessage);
  };


  const handleSubmit = () => {
    if (!message) {
      return;
    }
    socket.emit("data", { message, user,});
    setMessage("");
  };

  const handleUpvote = (messageId) => {
    // Call the upvote API endpoint
    fetch(`http://localhost:5001/upvote/${messageId}`, { method: 'POST' })
      .then(response => response.json())
      .then(data => console.log(data))
      .catch(error => console.error(error));
  };

  const handleDownvote = (messageId) => {
    // Call the downvote API endpoint
    fetch(`http://localhost:5001/downvote/${messageId}`, { method: 'POST' })
      .then(response => response.json())
      .then(data => console.log(data))
      .catch(error => console.error(error));
  };


  useEffect(() => {

    socket.on("data", (data) => {
      console.log('data',data)
      setMessages([...messages, data]);
    });


    return () => {
      socket.off("data", () => {
        console.log("data event was removed");
      });

    };
  }, [socket, messages]);

  return (
    <div>
      <h2>WebSocket Communication</h2>
      <input type="text" value={message} onChange={handleText} />
      <button onClick={handleSubmit}>submit</button>
      <ul>
        {messages.map((msg, ind) => (
          <li key={ind}>
            {msg.user}: {msg.message} :: {msg.ulid} :: {msg.id}
            Upvotes: {msg.upvotes} | Downvotes: {msg.downvotes}
            <button onClick={() => handleUpvote(msg.ulid)}>Upvote</button>
            <button onClick={() => handleDownvote(msg.ulid)}>Downvote</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
