import { useContext, useEffect, useState } from "react";
import UserContext from "../UserContext";
import './WebSocketCall.css';

export default function WebSocketCall({ socket }) {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const user = useContext(UserContext)

  const handleText = (e) => {
    const inputMessage = e.target.value;
    setMessage(inputMessage);
  };


  const handleSubmit = () => {
    if (!message) {
      return;
    }
    socket.emit("data", { message, user,});
    setMessage("");
  };

  const handleUpvote = (msgulid) => {
    socket.emit("upvote", {msgulid});
  };

  const handleDownvote = (msgulid) => {
    console.log(msgulid)
    socket.emit("downvote", {msgulid});
  };


  useEffect(() => {


    socket.on("data", (data) => {
      //console.log('data',data)
      setMessages([...messages, data]);
    });

    socket.on("upvote", (data) => {
      //console.log('from upvote',data)

      // Find the index of the message with the matching ulid in the messages array
      const messageIndex = messages.findIndex(message => message.ulid === data.ulid);
      console.log(messageIndex)
      if (messageIndex !== -1) {
          // If the message with the matching ulid is found, update the upvote field
          const updatedMessages = [...messages];
          updatedMessages[messageIndex].upvote = data.upvote;

          // Update the state with the modified messages array
          setMessages(updatedMessages);
      }
    });

    socket.on("downvote", (data) => {
      console.log('from downvote',data)

      // Find the index of the message with the matching ulid in the messages array
      const messageIndex = messages.findIndex(message => message.ulid === data.ulid);
      console.log(messageIndex)
      if (messageIndex !== -1) {
          // If the message with the matching ulid is found, update the upvote field
          const updatedMessages = [...messages];
          updatedMessages[messageIndex].downvote = data.downvote;

          // Update the state with the modified messages array
          setMessages(updatedMessages);
      }
    });


    return () => {
      socket.off("data", () => {
        console.log("data event was removed");
      });

    };
  }, [socket, messages]);

  return (
    <div>
      <h2>WebSocket Communication</h2>
      <ul>
        {messages.map((msg, ind) => (
          <li key={ind}>
            <div className="message-container">
              <div className="user-message">
                {msg.user}: {msg.message}   
              </div>
              <div className="vote-buttons">
                Upvotes: {msg.upvote} | Downvotes: {msg.downvote}
                <button onClick={() => handleUpvote(msg.ulid)}>Up</button>
                <button onClick={() => handleDownvote(msg.ulid)}>Down</button>
              </div>
              </div>
          </li>
        ))}
      </ul>
      <div className="bottom-fixed-container">
        <input type="text" value={message} onChange={handleText} />
        <button onClick={handleSubmit}>submit</button>
      </div>
    </div>
  );
}
