import { useContext, useEffect, useState } from "react";
import UserContext from "../UserContext";

export default function WebSocketCall({ socket }) {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [votes, setVotes] = useState({});
  const user = useContext(UserContext)

  const handleText = (e) => {
    const inputMessage = e.target.value;
    setMessage(inputMessage);
  };

  const handleUpvote = (messageId) => {
    socket.emit("upvote", { message_id: messageId, user, votes });
  };

  const handleDownvote = (messageId) => {
    socket.emit("downvote", { message_id: messageId, user, votes });
  };

  const handleSubmit = () => {
    if (!message) {
      return;
    }
    socket.emit("data", { message, user, votes});
    setMessage("");
  };


  useEffect(() => {

    socket.on("data", (data) => {
      setMessages([...messages, data.data]);
    });

    socket.on("upvote", (data) => {
      setVotes({ ...votes, [data.message_id]: data.upvotes });
    });

    socket.on("downvote", (data) => {
      setVotes({ ...votes, [data.message_id]: data.downvotes });
    });

    socket.on("vote_error", (data) => {
      console.error("Vote error:", data.error);
    });

    return () => {
      socket.off("data", () => {
        console.log("data event was removed");
      });
      socket.off("upvote");
      socket.off("downvote");

    };
  }, [socket, messages, votes]);

  return (
    <div>
      <h2>WebSocket Communication</h2>
      <input type="text" value={message} onChange={handleText} />
      <button onClick={handleSubmit}>submit</button>
      <ul>
        {messages.map((msg, ind) => (
          <li key={ind}>
            {msg.user}: {msg.message}
            Votes: {votes[msg.id] || 0}
          </li>
        ))}
      </ul>
    </div>
  );
}
