import { useContext, useEffect, useState } from "react";
import UserContext from "../UserContext";

export default function WebSocketCall({ socket }) {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [votes, setVotes] = useState({});
  const user = useContext(UserContext)

  const handleText = (e) => {
    const inputMessage = e.target.value;
    setMessage(inputMessage);
  };

  const handleSubmit = () => {
    if (!message) {
      return;
    }
    socket.emit("data", { message, user, votes: 0 });
    setMessage("");
  };

  const handleVote = (messageId, voteType) => {
    socket.emit("vote", { messageId, voteType });
  };

  useEffect(() => {

    socket.on("data", (data) => {
      setMessages([...messages, data.data]);
      setVotes((prevVotes) => ({ ...prevVotes, [data.id]: data.votes }));
    });

    socket.on("vote", (data) => {
      setVotes((prevVotes) => ({ ...prevVotes, [data.messageId]: data.votes }));
    });

    return () => {
      socket.off("data", () => {
        console.log("data event was removed");
      });
      socket.off("vote");

    };
  }, [socket, messages]);

  return (
    <div>
      <h2>WebSocket Communication</h2>
      <input type="text" value={message} onChange={handleText} />
      <button onClick={handleSubmit}>submit</button>
      <ul>
        {messages.map((msg, ind) => (
          <li key={ind}>
            {msg.user}: {msg.message}
            <button onClick={() => handleVote(msg.id, "upvote")}>Upvote</button>
            <button onClick={() => handleVote(msg.id, "downvote")}>Downvote</button>
            Votes: {votes[msg.id] || 0}
          </li>
        ))}
      </ul>
    </div>
  );
}
